def oracle(MAB):
    real_means = [m.mean for m in MAB]
    p_star = np.max(real_means)
    C_p=[]
    for p in real_means:
        if p!=p_star: 
            KL= p*np.log(p/p_star) + (1-p) * np.log((1-p)/(1-p_star))
            C_p.append((p_star - p)/KL)

    C_p = np.array(C_p)
    return np.sum(C_p)
